<?php use PHPMailer\PHPMailer\PHPMailer;use PHPMailer\PHPMailer\Exception;use PHPMailer\PHPMailer\SMTP;require'PHPMailer/src/PHPMailer.php';require'PHPMailer/src/Exception.php';require'PHPMailer/src/SMTP.php';function sanitize_input($input){$input=trim($input);$input=stripslashes($input);$input=htmlspecialchars($input);return$input;}$name=sanitize_input(getenv("NAME"));$email=sanitize_input(getenv("EMAIL"));$message=sanitize_input(getenv("MESSAGE"));try{$mail=new PHPMailer(true);$mail->SMTPDebug=0;$mail->isSMTP();$mail->Host='smtp.gmail.com';$mail->SMTPAuth=true;$mail->Username=getenv("SMTP_ADRESS");$mail->Password=getenv("SMTP_P");$mail->SMTPSecure='tls';$mail->Port=587;$mail->setFrom("FORMULAIRE.SITE.NOUVEAU.MESSAGE@NePasRepondre.ok");$mail->addAddress(getenv("SMTP_ADRESS"));$mail->Subject="Nouveaux message formulaire de contact";$message="Message : $name\n\n$email\n\n$message\n\n";$mail->Body=$message;$mail->send();}catch(Exception$e){}?>
